public class Exam {
      public String mystery(String dna) {
      int pos = dna.indexOf("T");
      int count = 0;
      int startPos = 0;
      String newDna = "";
      if (pos == -1) {
        return dna;
      }
      while (count < 3) {
        count += 1;
        newDna = newDna + dna.substring(startPos,pos);
        startPos = pos+1;
        pos = dna.indexOf("T", startPos);
        if (pos == -1) {
          break;
        }
      }
      newDna = newDna + dna.substring(startPos);
      return newDna;
}
    public int Counting(String str){
        return str.length();
    }
    public void  testOn(){
        String str = ("ATGATCTAATTTATGCTGCAACGGTGAAGA");
        System.out.println(mystery(str));
        String str2 = ("");
        System.out.println(Counting(str2));
        String str3 = ("");
        System.out.println(Counting(str3));
    }
}
